import React, { useState } from "react";
import "./RandomPage.css";

const dummyImages = [
  "https://via.placeholder.com/600x400?text=Image+1",
  "https://via.placeholder.com/600x400?text=Image+2",
  "https://via.placeholder.com/600x400?text=Image+3",
  "https://via.placeholder.com/600x400?text=Image+4",
  "https://via.placeholder.com/600x400?text=Image+5",
];

function RandomPage() {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % dummyImages.length);
  };

  const handlePrev = () => {
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + dummyImages.length) % dummyImages.length
    );
  };

  return (
    <div className="random-page-container">
      <button onClick={handlePrev} className="random-page-button left">
        ◀
      </button>
      <img
        src={dummyImages[currentIndex]}
        alt="Random"
        className="random-page-image"
      />
      <button onClick={handleNext} className="random-page-button right">
        ▶
      </button>
    </div>
  );
}

export default RandomPage;
