import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaUser } from "react-icons/fa"; // 사람 아이콘 추가
import "./Header.css";

function Header() {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleModalToggle = () => {
    setIsModalOpen(!isModalOpen);
  };

  const handleModalClose = (e) => {
    // 모달 외부 영역을 클릭하면 닫힘
    if (e.target.className === "modal") {
      setIsModalOpen(false);
    }
  };

  return (
    <div className="header-container">
      <Link to="/myfeed" className="header-title">
        DAT
      </Link>
      <nav className="header">
        <Link to="/" className="header-link">
          Feed
        </Link>
        <Link to="/myfeed" className="header-link">
          MyFeed
        </Link>
        <Link to="/team" className="header-link">
          TeamPage
        </Link>
        <button onClick={handleModalToggle} className="login-icon">
          <FaUser />
        </button>
      </nav>

      {isModalOpen && (
        <div className="modal" onClick={handleModalClose}>
          <div className="modal-content">
            <h2>Social Login</h2>
            <button className="social-login-button">Login with Google</button>
            <button className="social-login-button">Login with Facebook</button>
            <button className="social-login-button">Login with Twitter</button>
            <button onClick={handleModalToggle} className="close-modal">
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Header;
