import React, { useState, useEffect } from "react";
import "./RandomPage.css";

function RandomPage() {
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    fetch("/api/images") // 서버에서 이미지를 가져오는 API 엔드포인트
      .then((response) => response.json())
      .then((data) => setImages(data.sort(() => Math.random() - 0.5)));
  }, []);

  const handleNext = () => setCurrentIndex((currentIndex + 1) % images.length);
  const handlePrev = () =>
    setCurrentIndex((currentIndex - 1 + images.length) % images.length);

  return (
    <div className="random-page-container">
      <button onClick={handlePrev} className="random-page-button">
        ◀
      </button>
      {images.length > 0 && (
        <img
          src={images[currentIndex]}
          alt="Random"
          className="random-page-image"
        />
      )}
      <button onClick={handleNext} className="random-page-button">
        ▶
      </button>
    </div>
  );
}

export default RandomPage;
